INSERT INTO appleDB.review (product_id, reviewer, rating, comment) VALUES(3, '박준형', 3, '기대했던 것보다는 평범한 편이에요. 다음 모델은 개선이 필요할 것 같습니다.');
INSERT INTO appleDB.review (product_id, reviewer, rating, comment) VALUES(4, '최혜진', 5, '완벽한 제품입니다. 다른 제품과 비교해도 월등한 성능을 자랑합니다.');
