INSERT INTO appleDB.review (product_id, reviewer, rating, comment) VALUES(6, '홍길동', 4, '일반적인 사용에는 충분히 좋은 성능을 보여줍니다. 가격 대비 만족도가 높습니다.');
INSERT INTO appleDB.review (product_id, reviewer, rating, comment) VALUES(7, '윤서아', 3, '기본적인 기능은 잘 작동하지만, 특별히 눈에 띄는 점은 없습니다. 중간 정도의 평가를 줍니다.');
INSERT INTO appleDB.review (product_id, reviewer, rating, comment) VALUES(8, '장민호', 4, '이 제품은 사용하기 쉽고 디자인도 멋집니다. 배터리 수명이 조금 더 길었으면 좋겠어요.');
