INSERT INTO appleDB.review (product_id, reviewer, rating, comment) VALUES(10, '김예림', 3, '전반적으로 괜찮은 제품이지만, 가격 대비 기능이 조금 부족하다고 느꼈습니다. 가성비를 중시하는 사용자에게는 다른 옵션을 추천합니다.');
